var productIdentifiers = properties.get('odata.productIdentifiers').split(',');
var isValid = false;
var queryProductId = message.getHeaders().get('productIdentifier');

for (var i = 0; i < productIdentifiers.length; i++) {
    if (productIdentifiers[i] === queryProductId) {
        isValid = true;
        break;
    }
}

message.setProperty('isValidProduct', isValid);

if (!message.getProperty('isValidProduct')) {
    var productId = message.getHeaders().get('productIdentifier');
    var errorResponse = '{"status": "error", "message": "The product identifier ' + productId + ' was not found.", "errorCode": "PRODUCT_NOT_FOUND"}';
    message.setBody(errorResponse);
}

transformations.script:
import com.sap.gateway.ip.core.customdev.util.Message;

def Message processData(Message message) {
    def isValid = message.getProperty('isValidProduct');
    def productId = message.getHeaders().get('productIdentifier');

    // Store productIdentifier in a property for use in OData query
    message.setProperty('productIdentifier', productId);

    // Log the request
    if (isValid) {
        def logMessage = "Processing valid request for product ID: " + productId;
        println(logMessage);
    } else {
        def logMessage = "Invalid product ID received: " + productId;
        println(logMessage);
    }

    return message;
}
