config.content:
if (message.getProperty('isExistProduct')) {
    println('Processing request for product: ' + message.getHeaders().get('productIdentifier'));
} else {
    println('Product identifier not found or incorrectly passed: ' + message.getHeaders().get('productIdentifier'));
}
return message;

def productIdentifiers = property.get('odata.productIdentifiers').split(',');
def requestedProductId = message.getHeaders().get('productIdentifier');

def isValid = productIdentifiers.find { it == requestedProductId } != null;

message.setProperty('isExistProduct', isValid);
return message;

if (message.getProperty('isExistProduct')) {
    // Keep the response as is, it's already in the right format
    return message;
} else {
    // Build error response
    def errorResponse = [
        status: "error",
        message: "The product identifier " + message.getHeaders().get('productIdentifier') + " was not found.",
        errorCode: "PRODUCT_NOT_FOUND"
    ];

    message.setBody(groovy.json.JsonOutput.toJson(errorResponse));
    return message;
}

transformations.script:
import com.sap.gateway.ip.core.customdev.util.Message;

def Message processData(Message message) {
    def productId = message.getHeaders().get('productIdentifier');

    // Only build the query if the product is valid
    if (message.getProperty('isExistProduct')) {
        def filterParam = "ProductId eq '" + productId + "'";
        def selectParam = "ProductId,Category,CategoryName,CurrencyCode,DimensionDepth,DimensionHeight,DimensionUnit,DimensionWidth,LongDescription,Name,PictureUrl,Price,QuantityUnit,ShortDescription,SupplierId,Weight,WeightUnit";

        message.setHeader('filterParam', filterParam);
        message.setHeader('selectParam', selectParam);
    }

    return message;
}
