import com.sap.gateway.ip.core.customdev.util.Message;

def Message processData(Message message) {
    def map = message.getProperties();
    def productIdentifier = message.getProperty("productIdentifier");
    def isExistProduct = message.getProperty("isExistProduct");

    def logMessage = "Processing request for product: " + productIdentifier;
    logMessage += ", Valid product: " + isExistProduct;

    println(logMessage);
    return message;
}

import com.sap.gateway.ip.core.customdev.util.Message;
import groovy.json.JsonBuilder;

def Message processData(Message message) {
    def productIdentifier = message.getProperty("productIdentifier");

    def errorResponse = [
        status: "error",
        message: "The product identifier " + productIdentifier + " was not found.",
        errorCode: "PRODUCT_NOT_FOUND"
    ];

    def jsonBuilder = new JsonBuilder(errorResponse);
    message.setBody(jsonBuilder.toString());
    message.setHeader("Content-Type", "application/json");

    return message;
}
