config.content:
if (message.getProperty('isExistProduct')) {
    message.setHeader('LogMessage', 'The request is processed and sent downstream with the product identifier ' + message.getHeaders().get('productIdentifier'));
} else {
    message.setHeader('LogMessage', 'The product identifier ' + message.getHeaders().get('productIdentifier') + ' was not passed in the request or was passed incorrectly.');
}
return message;

def productIdentifiers = property.get('odata.productIdentifiers').split(',');
def requestedProductId = message.getHeaders().get('productIdentifier');
def isValid = productIdentifiers.contains(requestedProductId);
message.setProperty('isExistProduct', isValid);
return message;

if (!message.getProperty('isExistProduct')) {
    def errorResponse = '{"status": "error", "message": "The product identifier ' + message.getHeaders().get('productIdentifier') + ' was not found.", "errorCode": "PRODUCT_NOT_FOUND"}';
    message.setBody(errorResponse);
    message.setHeader('Content-Type', 'application/json');
} else {
    message.setHeader('Content-Type', 'application/json');
}
return message;

