config.content:
if (message.getProperty("isExistProduct")) {
    message.setProperty("routeId", "valid_product");
} else {
    message.setProperty("routeId", "invalid_product");
}
return message;

def productId = message.getHeaders().get("productIdentifier");
message.setProperty("SAP_OData_Filter", "ProductId eq '" + productId + "'");
message.setProperty("SAP_OData_Select", "ProductId,Category,CategoryName,CurrencyCode,DimensionDepth,DimensionHeight,DimensionUnit,DimensionWidth,LongDescription,Name,PictureUrl,Price,QuantityUnit,ShortDescription,SupplierId,Weight,WeightUnit");
return message;

def productIdentifiers = property.get("odata.productIdentifiers").split(",");
def productId = message.getHeaders().get("productIdentifier");
def isExistProduct = productIdentifiers.find { it == productId } != null;
message.setProperty("isExistProduct", isExistProduct);
return message;

def productId = message.getHeaders().get("productIdentifier");
def errorResponse = '{"status": "error", "message": "The product identifier ' + productId + ' was not found.", "errorCode": "PRODUCT_NOT_FOUND"}';
message.setBody(errorResponse);
message.setHeader("Content-Type", "application/json");
return message;

