dataprocessscript.script:
import java.util.Properties;
import java.io.InputStream;
import com.boomi.execution.ExecutionUtil;
import com.boomi.execution.ExecutionManager;
propValue = ExecutionUtil.getDynamicProcessProperty("DemographicErrorCount");
int intValue = Integer.parseInt(propValue);
for( int i = 0; i < dataContext.getDataCount(); i++ )
{
InputStream is = dataContext.getStream(i);
Properties props = dataContext.getProperties(i);
dataContext.storeStream(is, props);
intValue = intValue + 1;
}
propValue = Integer.toString(intValue);
ExecutionUtil.setDynamicProcessProperty("DemographicErrorCount", propValue, false);
